def assert_equal expected, got
  puts expected == got ?
    "passed: #{expected}" :
    "Assertion failed\nExpected: #{expected}\nGot: #{got}"
end

require 'always_turn_left'

space = Space.new
assert_equal [0, 0] , space.position.to_a
assert_equal [0, 1] , space.go('W').position.to_a
assert_equal [0, 2] , space.go('W').position.to_a
assert_equal [0, 1] , space.back.go('W').position.to_a
assert_equal [0, 0] , space.go('W').position.to_a
assert_equal ["3"] , space.trace_wall

space = Space.new
assert_equal [0, 0] , space.position.to_a
space.walk 'WRWWLWWLWWLWLWRRWRWWWRWWRWLW'
assert_equal [-3, 4] , space.position.to_a
space.back
space.walk 'WWRRWLWLWWLWWLWWRWWRWWLW'
assert_equal [0, 0] , space.position.to_a
assert_equal ["ac5", "386", "9c7", "e43", "9c5"], space.trace_wall

